// --- Generate 10000 Students in various courses ---
export const STUDENTS: Student[] = Array.from({ length: 10000 }, (_, i) => {
    const allCourses = Object.values(COURSES).flat();
    const courseIndex = i % allCourses.length;
    const course = allCourses[courseIndex];
    
    const dept = Object.keys(COURSES).find(d => COURSES[d as keyof typeof COURSES].includes(course)) || 'Unknown';
    
    const semester = [1, 3, 5, 7, 9][i % 5];
    const isDebarred = (i + 1) % 50 === 0;

    return {
      id: `S${String(i + 1).padStart(5, '0')}`,
      name: generateName(),
      rollNo: `${(dept.substring(0,3) + (course.includes('B.Tech') ? 'BT' : '')).toUpperCase()}${String(new Date().getFullYear()).slice(2)}-${String(i + 1).padStart(5,'0')}`,
      department: dept,
      course: course,
      semester: semester,
      section: ['A', 'B', 'C', 'D', 'E', 'F', 'G', 'H'][i % 8],
      group: i % 2 === 0 ? 'A' : 'B', 
      ineligibilityRecords: [],
      unavailableSlots: [],
      seatAssignment: null,
      isDebarred: isDebarred,
    };
});

const generateBenchCapacities = (rows: number, cols: number, specialRows: number[] = [], specialCapacity: number = 3, defaultCapacity: number = 2): number[] => {
    const capacities: number[] = [];
    for (let r = 0; r < rows; r++) {
        for (let c = 0; c < cols; c++) {
            capacities.push(specialRows.includes(r + 1) ? specialCapacity : defaultCapacity);
        }
    }
    return capacities;
};

